// export default Dashboard;

import React, { useContext, useEffect, useState } from "react";
import { AuthContext } from "../contexts/AuthContext";

const Dashboard = () => {
  const { token, setToken } = useContext(AuthContext);
  const [contacts, setContacts] = useState([]);
  const [form, setForm] = useState({ name: "", email: "", contact: "" });
  const [msg, setMsg] = useState("");
  const [editingId, setEditingId] = useState(null);

  // Fetch contacts
  const getContacts = async () => {
    setMsg("");
    try {
      const res = await fetch("/api/contacts", {
        headers: { Authorization: `Bearer ${token}` },
      });
      if (!res.ok) throw new Error("Failed to fetch contacts");
      setContacts(await res.json());
    } catch (err) {
      setMsg(err.message);
    }
  };

  useEffect(() => {
    getContacts();
    // eslint-disable-next-line
  }, [token]);

  const handleChange = e => setForm({ ...form, [e.target.name]: e.target.value });

  const handleSubmit = async e => {
    e.preventDefault();
    setMsg("");
    try {
      const res = await fetch("/api/contacts", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify(form),
      });
      if (!res.ok) throw new Error("Failed to add contact");
      setForm({ name: "", email: "", contact: "" });
      setMsg("Contact added!");
      getContacts();
    } catch (err) {
      setMsg(err.message);
    }
  };

  const handleDelete = async id => {
    setMsg("");
    try {
      const res = await fetch(`/api/contacts/${id}`, {
        method: "DELETE",
        headers: { Authorization: `Bearer ${token}` },
      });
      if (!res.ok) throw new Error("Failed to delete contact");
      setMsg("Contact deleted!");
      getContacts();
    } catch (err) {
      setMsg(err.message);
    }
  };

  const handleEdit = contact => {
    setEditingId(contact._id);
    setForm({ name: contact.name, email: contact.email, contact: contact.contact });
  };

  const handleUpdate = async e => {
    e.preventDefault();
    setMsg("");
    try {
      const res = await fetch(`/api/contacts/${editingId}`, {
        method: "PUT",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify(form),
      });
      if (!res.ok) throw new Error("Failed to update contact");
      setEditingId(null);
      setForm({ name: "", email: "", contact: "" });
      setMsg("Contact updated!");
      getContacts();
    } catch (err) {
      setMsg(err.message);
    }
  };

  const handleLogout = () => {
    setToken(null);
  };

  return (
    <div style={{ background: "#1e1e2f", color: "#fff", minHeight: "100vh", padding: "20px" }}>
      <h2 style={{ textAlign: "center", color: "#00ff88" }}>Dashboard</h2>
      <button
        onClick={handleLogout}
        style={{
          background: "#ff4444",
          color: "#fff",
          border: "none",
          padding: "10px 20px",
          borderRadius: "5px",
          cursor: "pointer",
          marginBottom: "20px",
        }}
      >
        Logout
      </button>

      <h3 style={{ color: "#00ff88" }}>{editingId ? "Edit Contact" : "Add Contact"}</h3>
      <form
        onSubmit={editingId ? handleUpdate : handleSubmit}
        style={{ display: "flex", flexDirection: "column", gap: "10px", maxWidth: "400px" }}
      >
        <input
          name="name"
          placeholder="Name"
          value={form.name}
          onChange={handleChange}
          required
          style={{ padding: "10px", borderRadius: "5px", border: "1px solid #555", background: "#2e2e3f", color: "#fff" }}
        />
        <input
          name="email"
          placeholder="Email"
          value={form.email}
          onChange={handleChange}
          required
          style={{ padding: "10px", borderRadius: "5px", border: "1px solid #555", background: "#2e2e3f", color: "#fff" }}
        />
        <input
          name="contact"
          placeholder="Contact"
          value={form.contact}
          onChange={handleChange}
          required
          style={{ padding: "10px", borderRadius: "5px", border: "1px solid #555", background: "#2e2e3f", color: "#fff" }}
        />
        <button
          type="submit"
          style={{
            background: "#00ff88",
            color: "#000",
            padding: "10px",
            border: "none",
            borderRadius: "5px",
            cursor: "pointer",
            fontWeight: "bold",
          }}
        >
          {editingId ? "Update" : "Add"}
        </button>
        {editingId && (
          <button
            type="button"
            onClick={() => {
              setEditingId(null);
              setForm({ name: "", email: "", contact: "" });
            }}
            style={{
              background: "#555",
              color: "#fff",
              padding: "10px",
              border: "none",
              borderRadius: "5px",
              cursor: "pointer",
            }}
          >
            Cancel
          </button>
        )}
      </form>

      {msg && <div style={{ marginTop: "10px", color: "#ffcc00" }}>{msg}</div>}

      <h3 style={{ marginTop: "30px", color: "#00ff88" }}>Contacts</h3>
      <ul style={{ listStyle: "none", padding: 0 }}>
        {contacts.map(c => (
          <li
            key={c._id}
            style={{
              background: "#2e2e3f",
              marginBottom: "10px",
              padding: "10px",
              borderRadius: "8px",
              display: "flex",
              justifyContent: "space-between",
              alignItems: "center",
            }}
          >
            <span>
              <strong>{c.name}</strong> - {c.email} - {c.contact}
            </span>
            <div>
              <button
                onClick={() => handleEdit(c)}
                style={{
                  background: "#ffaa00",
                  color: "#000",
                  border: "none",
                  padding: "5px 10px",
                  marginRight: "5px",
                  borderRadius: "5px",
                  cursor: "pointer",
                }}
              >
                Edit
              </button>
              <button
                onClick={() => handleDelete(c._id)}
                style={{
                  background: "#ff4444",
                  color: "#fff",
                  border: "none",
                  padding: "5px 10px",
                  borderRadius: "5px",
                  cursor: "pointer",
                }}
              >
                Delete
              </button>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Dashboard;
